mapper.select:
	SELECT * 
	FROM TBL_NOTE N JOIN TBL_TEAM T
	WHERE N.N_IDX = T.N_IDX 
		AND 1=1 	     	
	
    	AND T.EMAIL = #{EMAIL}
    
    
    	AND N.DEAD = #{DEAD}

mapper.update:
UPDATE TBL_NOTE
SET DEAD = 'COMPLETE'
WHERE N_IDX = #{N_IDX}

mapper.select:
	SELECT *
	FROM TBL_NOTE_DETAIL 
	WHERE N_IDX = #{N_IDX}
	
    	AND P_IDX = #{P_IDX}
    
    
    	AND P_IDX = '1'

SELECT count(*)
FROM TBL_NOTE_DETAIL 
WHERE N_IDX = #{N_IDX}

SELECT CONTENT
FROM TBL_NOTE_DETAIL
WHERE N_IDX = #{N_IDX} AND P_IDX = #{P_IDX}

mapper.update:
UPDATE TBL_NOTE_DETAIL
SET N_IDX = #{N_IDX} , P_IDX =#{P_IDX} , CONTENT =#{CONTENT}
WHERE N_IDX = #{N_IDX} AND P_IDX = #{P_IDX}

UPDATE TBL_COMMENT
SET CONTENT = #{CONTENT}
WHERE C_IDX = #{C_IDX}
